import { description } from './Arrays'
import { Names } from './Arrays'
import { Colors } from './Arrays'
import { Images } from './Arrays'
import { Categories } from './Arrays'

export const generateProducts = (n = 11) => {
  const products = []
  for (let i = 0; i < n; i++) {
    products.push(generateProduct())
  }

  return products
}

const generateProduct = () => {
  return {
    key: getRandomId(),
    name: getRandomElement(Names),
    description: getRandomDescription(),
    color: getRandomElement(Colors),
    category: getRandomElement(Categories),
    price: getRandomNumber(10, 9999),
    rate: (Math.random() * 5).toFixed(1),
    img: getRandomElement(Images),
  }
}

const getRandomId = () => {
  return Date.now() + getRandomString()
}

const getRandomString = () => {
  return Math.random().toString(36).substring(2)
}

const getRandomElement = (array) => {
  return array[Math.floor(Math.random() * array.length)]
}

const getRandomNumber = (start = 1, end = 100) => {
  return Math.floor(Math.random() * (end - start)) + start
}

const getRandomDescription = () => {
  const array = []
  for (let i = 0; i < 10; i++) {
    array.push(getRandomElement(description))
  }
  return array.join(' ')
}
