import styles from './App.module.css';
import React, { useState, useEffect } from 'react';
import Product from './Product.js';
import { generateProducts } from './Generator.js';
import { sortByRate, sortToCheapest, sortToExpensive } from './Sorts.js';
import Filter from './Filter';

export default function App() {
  const [products, setProducts] = useState(generateProducts(5));
  const [color, setColor] = useState([]);
  const [value, setValue] = useState('');
  const [range, setRange] = useState({ min: 10, max: 9999 });
  const [sorting, setSorting] = useState('active');
  const [category, setCategory] = useState([]);

  useEffect(() => {
    setDefaultSort();
  }, []);

  const setDefaultSort = () => {
    setProducts(sortToCheapest(products).concat([]));
  };

  const handleSetFilterByColor = () => {
    const arr = [];
    const filters = [];
    products.forEach((product) =>
      !arr.includes(product.color) ? arr.push(product.color) : {}
    );

    arr.forEach((el) => filters.push({ name: el, done: false }));

    return filters;
  };
  const [colorFilter, setColorFilter] = useState(handleSetFilterByColor());

  const handleSetFilterByCategory = () => {
    const arr = [];
    const filters = [];
    products.forEach((product) =>
      !arr.includes(product.category) ? arr.push(product.category) : {}
    );

    arr.forEach((el) => filters.push({ name: el, done: false }));

    return filters;
  };
  const [categoryFilter, setCategoryFilter] = useState(handleSetFilterByCategory());

  const customProducts = products.filter(
    (product) =>
      (color.includes(product.color) || color.length === 0) &&
      (category.includes(product.category) || category.length === 0) &&
      product.name.toLowerCase().includes(value) &&
      product.price <= range.max &&
      product.price >= range.min
  );

  const allCount = customProducts.length;

  const createSortDiv = (e) => {
    for (const child of e.target.closest('div').children) {
      child.className = 'usual';
    }
    e.target.className = 'active';
  };

  const handleSortToCheapest = (e) => {
    setProducts(sortToCheapest(products).concat([]));
    createSortDiv(e);
  };

  const handleSortToExpensive = (e) => {
    setProducts(sortToExpensive(products).concat([]));
    createSortDiv(e);
    setSorting('usual');
  };

  const handleSortByRate = (e) => {
    setProducts(sortByRate(products).concat([]));
    createSortDiv(e);
    setSorting('usual');
  };

  const handleSetDone = (done, name, filterType) => {
    if (filterType === 'color') {
      setColorFilter(
        colorFilter.map((color) => (color.name === name ? { name, done } : color))
      );
      done
        ? setColor(color.concat([name]))
        : setColor(color.filter((el) => color.indexOf(name) !== color.indexOf(el)));
    } else if (filterType === 'category') {
      setCategoryFilter(
        categoryFilter.map((category) =>
          category.name === name ? { name, done } : category
        )
      );
      done
        ? setCategory(category.concat([name]))
        : setCategory(category.filter((el) => category.indexOf(name) !== category.indexOf(el)));
    }
  };

  const handleSetValue = (e) => {
    setValue(e.target.value.trim().toLowerCase());
  };

  const handleSetMin = (e) => {
    setRange({ min: e.target.value, max: range.max });
  };

  const handleSetMax = (e) => {
    e.target.value
      ? setRange({ min: range.min, max: e.target.value })
      : setRange({ min: range.min, max: 9999 });
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <div className="counters">Всего товаров: {allCount}</div>
        <input
          type="text"
          className={styles.search}
          placeholder="Поиск"
          onChange={handleSetValue}
        ></input>
      </div>
      <div className={styles.content}>
        <div className={styles.leftColumn}>
          <span className={styles.filterName}>По цвету</span>
          <div className={styles.filterValues}>
            {colorFilter.map((filter) => (
              <Filter
                name={filter.name}
                done={filter.done}
                onDone={(done) => handleSetDone(done, filter.name, 'color')}
              />
            ))}
          </div>
          <span className={styles.filterName}>По категории</span>
          <div className={styles.filterValues}>
            {categoryFilter.map((filter) => (
              <Filter
                name={filter.name}
                done={filter.done}
                onDone={(done) => handleSetDone(done, filter.name, 'category')}
              />
            ))}
          </div>
          <span className={styles.filterName}>По цене</span>
          <div className={styles.filterRange}>
            <input
              type="number"
              placeholder="от"
              onChange={handleSetMin}
              min={10}
              max={9999}
            ></input>
            -
            <input
              type="number"
              placeholder="до"
              onChange={handleSetMax}
              min={10}
              max={9999}
            ></input>
          </div>
        </div>
        <div className={styles.rightColumn}>
          <div className={styles.sorts}>
            <button onClick={handleSortToCheapest} className={sorting}>
              Сначала дорогие
            </button>
            <button onClick={handleSortToExpensive} className="usual">
              Сначала дешёвые
            </button>
            <button onClick={handleSortByRate} className="usual">
              Сначала популярные
            </button>
          </div>
          <div className={styles.products}>
            {!allCount ? (
              <div>По вашему запросу ничего не найдено</div>
            ) : (
              customProducts.map((product) => (
                <Product
                  name={product.name}
                  description={product.description}
                  price={product.price}
                  rate={product.rate}
                  category={product.category}
                  color={product.color}
                  img={product.img}
                />
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
