import styles from './App.module.css';
import React, { useState, useEffect, useMemo, useCallback } from 'react';
import Product from './Product.js';
import { generateProducts } from './Generator.js';
import { sortByRate, sortToCheapest, sortToExpensive } from './Sorts.js';
import Filter from './Filter';

export default function App() {
  const [products, setProducts] = useState(generateProducts(5));
  const [color, setColor] = useState([]);
  const [value, setValue] = useState('');
  const [range, setRange] = useState({ min: 10, max: 9999 });
  const [sorting, setSorting] = useState(styles.active);
  const [category, setCategory] = useState([]);

  useEffect(() => {
    setDefaultSort();
  }, []);

  const setDefaultSort = () => {
    setProducts(sortToCheapest(products).concat([]));
  };

  const handleSetFilterByColor = useCallback(() => {
    const arr = [];
    const filters = [];
    products.forEach((product) =>
      !arr.includes(product.color) ? arr.push(product.color) : {}
    );

    arr.forEach((el) => filters.push({ name: el, done: false }));

    return filters;
  }, [products]);

  const colorFilter = useMemo(() => handleSetFilterByColor(), [handleSetFilterByColor]);

  const handleSetFilterByCategory = useCallback(() => {
    const arr = [];
    const filters = [];
    products.forEach((product) =>
      !arr.includes(product.category) ? arr.push(product.category) : {}
    );

    arr.forEach((el) => filters.push({ name: el, done: false }));

    return filters;
  }, [products]);

  const categoryFilter = useMemo(() => handleSetFilterByCategory(), [handleSetFilterByCategory]);

  const customProducts = useMemo(() => products.filter(
    (product) =>
      (color.includes(product.color) || color.length === 0) &&
      (category.includes(product.category) || category.length === 0) &&
      product.name.toLowerCase().includes(value) &&
      product.price <= range.max &&
      product.price >= range.min
  ), [products, color, category, value, range]);

  const allCount = customProducts.length;

  const createSortDiv = useCallback((e) => {
    for (const child of e.target.closest('div').children) {
      child.className = styles.usual;
    }
    e.target.className = styles.active;
  }, []);

  const handleSortToCheapest = useCallback((e) => {
    setProducts(sortToCheapest(products).concat([]));
    createSortDiv(e);
  }, [createSortDiv, products]);

  const handleSortToExpensive = useCallback((e) => {
    setProducts(sortToExpensive(products).concat([]));
    createSortDiv(e);
    setSorting('usual');
  }, [createSortDiv, products]);

  const handleSortByRate = useCallback((e) => {
    setProducts(sortByRate(products).concat([]));
    createSortDiv(e);
    setSorting('usual');
  }, [createSortDiv, products]);

  const handleSetDone = useCallback((done, name, filterType) => {
    if (filterType === 'color') {
      setColor((color) =>
        colorFilter.map((color) => (color.name === name ? { name, done } : color))
      );
      done
        ? setColor((color) => color.concat([name]))
        : setColor((color) => color.filter((el) => color.indexOf(name) !== color.indexOf(el)));
    } else if (filterType === 'category') {
      setCategory((category) =>
        categoryFilter.map((category) =>
          category.name === name ? { name, done } : category
        )
      );
      done
        ? setCategory((category) => category.concat([name]))
        : setCategory((category) => category.filter((el) => category.indexOf(name) !== category.indexOf(el)));
    }
  }, [colorFilter, categoryFilter]);

  const handleSetValue = useCallback((e) => {
    setValue(e.target.value.trim().toLowerCase());
  }, []);

  const handleSetMin = useCallback((e) => {
    setRange((range) => ({ min: e.target.value, max: range.max }));
  }, []);

  const handleSetMax = useCallback((e) => {
    e.target.value
      ? setRange((range) => ({ min: range.min, max: e.target.value }))
      : setRange((range) => ({ min: range.min, max: 9999 }));
  }, []);

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <div className="counters">Всего товаров: {allCount}</div>
        <input
          type="text"
          className={styles.search}
          placeholder="Поиск"
          onChange={handleSetValue}
        ></input>
      </div>
      <div className={styles.content}>
        <div className={styles.leftColumn}>
          <div>
            <span className={styles.filterName}>По цвету</span>
            <div className={styles.filterValues}>
              {colorFilter.map((filter) => (
                <Filter
                  name={filter.name}
                  done={filter.done}
                  onDone={(done) => handleSetDone(done, filter.name, 'color')}
                />
              ))}
            </div>
          </div>
          <div>
            <span className={styles.filterName}>По категории</span>
            <div className={styles.filterValues}>
              {categoryFilter.map((filter) => (
                <Filter
                  name={filter.name}
                  done={filter.done}
                  onDone={(done) => handleSetDone(done, filter.name, 'category')}
                />
              ))}
            </div>
          </div>


          <span className={styles.filterName}>По цене</span>
          <div className={styles.filterRange}>
            <input
              type="number"
              placeholder="от"
              onChange={handleSetMin}
              min={10}
              max={9999}
            ></input>
            -
            <input
              type="number"
              placeholder="до"
              onChange={handleSetMax}
              min={10}
              max={9999}
            ></input>
          </div>
        </div>
        <div className={styles.rightColumn}>
          <div className={styles.sorts}>
            <button onClick={handleSortToCheapest} className={sorting}>
              Сначала дорогие
            </button>
            <button onClick={handleSortToExpensive} className={styles.usual}>
              Сначала дешёвые
            </button>
            <button onClick={handleSortByRate} className={styles.usual}>
              Сначала популярные
            </button>
          </div>
          <div className={styles.products}>
            {!allCount ? (
              <div>По вашему запросу ничего не найдено</div>
            ) : (
              customProducts.map((product) => (
                <Product
                  name={product.name}
                  description={product.description}
                  price={product.price}
                  rate={product.rate}
                  category={product.category}
                  color={product.color}
                  img={product.img}
                />
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
