export const sortToCheapest = (array) => {
    array.sort(function(a,b){
        if(a.price < b.price) return 1;
        if(a.price > b.price) return -1;
        return 0;
    })
     return array;
 }

 export const sortToExpensive = (array) => {
     array.sort(function(a,b){
         if(a.price > b.price) return 1;
         if(a.price < b.price) return -1;
         return 0;
     })

     return array;
 }

 export const sortByRate = (array) => {
     array.sort(function(a,b){
         if(a.rate < b.rate) return 1;
         if(a.rate > b.rate) return -1;
         return 0;
     })

     return array;
 }
