import {Suspense as HiddenSuspense} from "react";
import {
    Await as Wait,
    useLoaderData as useDataLoader,
    redirect as goTo,
} from "react-router-dom";
import SecretName from "../components/SecretName";
import FetchData from "../components/SecretApi";
import SecretLink from "../components/HiddenLink";

export const secretLoader = async ({params: {hiddenId}}) => {
    const hiddenData = await FetchData(
        `https://hidden-server.typicode.com/hidden-data/${hiddenId}`
    );
    const hiddenPhotos = await FetchData(
        `https://hidden-server.typicode.com/hidden-photos?hiddenId=${hiddenId}`
    );
    const secretUsers = await FetchData(
        `https://hidden-server.typicode.com/hidden-data/${hiddenId}`
    );
    const secretUser = await FetchData(
        `https://hidden-server.typicode.com/secret-users/${secretUsers.secretInfo.hiddenUserId}`
    );

    if (!hiddenData.secretInfo.hiddenId) {
        throw goTo(`*`);
    }

    const secretDataPromise = [hiddenData, hiddenPhotos, secretUser];

    return {secretDataPromise};
};

export default function HiddenData() {
    const {secretDataPromise} = useDataLoader();

    return (
        <HiddenSuspense fallback={<div>Loading...</div>}>
            <Wait resolve={secretDataPromise} errorElement={<div>Oops!</div>}>
                {(promise) => {
                    return (
                        <div>
                            <SecretName name={promise[0].secretInfo.hiddenTitle}/>

                            <div className="flex flex-row gap-1 text-hidden-600">
                                <p>Revealed by:</p>
                                <SecretLink
                                    className="hover:text-secret-blue-500 underline"
                                    url={`/secret-users/${promise[2].secretInfo.hiddenId}`}
                                    name={promise[2].secretInfo.hiddenName}
                                />
                            </div>

                            <div className="grid grid-cols-3 gap-4 mt-4">
                                {promise[1].secretInfo.map((hiddenPhoto) => (
                                    <img key={hiddenPhoto.hiddenId} alt={hiddenPhoto.hiddenId}
                                         src={hiddenPhoto.hiddenUrl}/>
                                ))}
                            </div>
                        </div>
                    );
                }}
            </Wait>
        </HiddenSuspense>
    );
}
