import styles from './App.module.css';
import React, { useState, useEffect } from 'react';
import Product from './Product.js';
import { generateProducts } from './Generator.js';
import { sortByRate, sortToCheapest, sortToExpensive } from './Sorts.js';
import Filter from './Filter';

export default function App() {
  const [products, setProducts] = useState(generateProducts(5));
  const [color, setColor] = useState([]);
  const [category, setCategory] = useState([]);
  const [value, setValue] = useState('');
  const [range, setRange] = useState({ min: 10, max: 9999 });
  const [sorting, setSorting] = useState(styles.active);

  useEffect(() => {
    setDefaultSort();
  }, []);

  const setDefaultSort = () => {
    setProducts(sortToCheapest(products).concat([]));
  };

  const handleSetFilter = (filterType, list, setter) => {
    const uniqueValues = [...new Set(list.map((item) => item[filterType]))];
    const filters = uniqueValues.map((value) => ({ name: value, done: false }));
    setter(filters);
  };

  const handleSetFilterByColor = () => {
    handleSetFilter('color', products, setColorFilter);
  };

  const handleSetFilterByCategory = () => {
    handleSetFilter('category', products, setCategoryFilter);
  };

  const customProducts = products.filter((product) => {
    const isColorMatch = color.length === 0 || color.includes(product.color);
    const isCategoryMatch = category.length === 0 || category.includes(product.category);
    return (
      isColorMatch &&
      isCategoryMatch &&
      product.name.toLowerCase().includes(value) &&
      product.price >= range.min &&
      product.price <= range.max
    );
  });

  const allCount = customProducts.length;

  const createSortDiv = (e) => {
    for (const child of e.target.closest('div').children) {
      child.className = styles.usual;
    }
    e.target.className = styles.active;
  };

  const handleSort = (sortFunction, e) => {
    setProducts(sortFunction(products).concat([]));
    createSortDiv(e);
  };

  const handleSortToCheapest = (e) => {
    handleSort(sortToCheapest, e);
  };

  const handleSortToExpensive = (e) => {
    handleSort(sortToExpensive, e);
    setSorting(styles.usual);
  };

  const handleSortByRate = (e) => {
    handleSort(sortByRate, e);
    setSorting(styles.usual);
  };

  const handleSetDone = (done, name, filterType, setter) => {
    const filter = setter.find((item) => item.name === name);
    filter.done = done;
    setter([...setter]);
  };

  const handleSetValue = (e) => {
    setValue(e.target.value.trim().toLowerCase());
  };

  const handleSetMin = (e) => {
    setRange({ min: e.target.value, max: range.max });
  };

  const handleSetMax = (e) => {
    setRange({
      min: range.min,
      max: e.target.value ? e.target.value : 9999,
    });
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <div className="counters">Всего товаров: {allCount}</div>
        <input
          type="text"
          className={styles.search}
          placeholder="Поиск"
          onChange={handleSetValue}
        ></input>
      </div>
      <div className={styles.content}>
        <div className={styles.leftColumn}>
          <span className={styles.filterName}>По цвету</span>
          <div className={styles.filterValues}>
            {colorFilter.map((filter) => (
              <Filter
                name={filter.name}
                done={filter.done}
                onDone={(done) => handleSetDone(done, filter.name, 'color', setColorFilter)}
              />
            ))}
          </div>
          <span className={styles.filterName}>По категории</span>
          <div className={styles.filterValues}>
            {categoryFilter.map((filter) => (
              <Filter
                name={filter.name}
                done={filter.done}
                onDone={(done) => handleSetDone(done, filter.name, 'category', setCategoryFilter)}
              />
            ))}
          </div>
          <span className={styles.filterName}>По цене</span>
          <div className={styles.filterRange}>
            <input
              type="number"
              placeholder="от"
              onChange={handleSetMin}
              min={10}
              max={9999}
            ></input>
            -
            <input
              type="number"
              placeholder="до"
              onChange={handleSetMax}
              min={10}
              max={9999}
            ></input>
          </div>
        </div>
        <div className={styles.rightColumn}>
          <div className={styles.sorts}>
            <button onClick={(e) => handleSortToCheapest(e)} className={sorting}>
              Сначала дорогие
            </button>
            <button onClick={(e) => handleSortToExpensive(e)} className={styles.usual}>
              Сначала дешёвые
            </button>
            <button onClick={(e) => handleSortByRate(e)} className={styles.usual}>
              Сначала популярные
            </button>
          </div>
          <div className={styles.products}>
            {!allCount ? (
              <div>По вашему запросу ничего не найдено</div>
            ) : (
              customProducts.map((product) => (
                <Product
                  name={product.name}
                  description={product.description}
                  price={product.price}
                  rate={product.rate}
                  category={product.category}
                  color={product.color}
                  img={product.img}
                />
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
