import {Suspense} from "react";
import {
    Await,
    useLoaderData,
    redirect,
} from "react-router-dom";
import Name from "../../components/Name";
import FetchUrl from "../../utils/remoteFetcher";
import InfoLink from "../../components/InfoLink";

export const routeLoader = async ({params: {id}}) => {
    const album = await FetchUrl(
        BASE_URL + `albums/${id}`
    );
    const photos = await FetchUrl(
        BASE_URL + `photos?albumId=${id}`
    );
    const users = await FetchUrl(
        BASE_URL + `albums/${id}`
    );
    const user = await FetchUrl(
        BASE_URL + `users/${users.info.userId}`
    );

    if (!album.info.id || !users.info.id || !users.info.id) {
        throw redirect(`*`);
    }

    const infoPromise = [album, photos, user];

    return {infoPromise};
};

export default function Album() {
    let promise;
    ({infoPromise: promise} = useLoaderData());

    return <Suspense fallback={
        <div>Processing...</div>
    }>
        <Await resolve={promise} errorElement={
            <div>Ooops!</div>
        }>
            {([album, photos, user]) => {
                return <div>
                    <Name name={album.info.title}/>

                    <div className="flex flex-row gap-1 text-stone-600">
                        <p>Created by: </p>
                        <InfoLink
                            className="hover:text-blue-500 underline"
                            url={`/users/${user.info.id}`}
                            name={user.info.name}
                        />
                    </div>

                    <div className="grid grid-cols-3 gap-4 mt-4">
                        {photos.info.map((photo) =>
                            <img key={photo.id} alt={photo.id} src={photo.url}/>
                        )}
                    </div>
                </div>;
            }}
        </Await>
    </Suspense>;
}
